<?php if (!defined('BASEPATH')) exit('No direct script access allowed');/** */class A_authors_m extends MY_Model {        /**     * Constructor method     *     * @access public     * @return void     */    function __construct()    {        parent::__construct();    }            public function create_auth($data)    {        return $this->insert($data, TRUE);    }        public function get_all_auth()    {        return $this->order_by("{$this->_table}.name", 'ASC')                    ->get_all();    }        public function update($id, $data)    {		return parent::update($id, array(			'name'    => $data['name'],			'email' => $data['email']		), TRUE);    }		public function insert($input){			return parent::insert(array(			'name'    => $input['name'],			'email' => $input['email']		));	}		function authors_options()    {        $authors = $this->get_all_auth();                $options = array();                if(!empty($authors))        {            foreach($authors as $author)            {                $options[$author->id] = $author->name;            }        }                return $options;    }}